mapper.select:
SELECT id, user_id, position_id, created_at, updated_at, title, resume_col, resume_row, end_time
FROM resume
WHERE user_id = #{userId};

SELECT id, user_id, position_id, created_at, updated_at, title, resume_col, resume_row, end_time
FROM resume
WHERE id = #{resumeId};

SELECT id, resume_id, order_num, question_content, answer_content, question_limit
FROM answer
WHERE resume_id = #{resumeId};

SELECT COUNT(*)
FROM answer
WHERE resume_id = #{resumeId};

SELECT resume_id
FROM answer
WHERE id = #{answerId}

mapper.update:
UPDATE answer
SET question_content = #{questionContent}, answer_content = #{answerContent}, order_num = #{orderNum}, question_limit = #{questionLimit}
WHERE id = #{id}

UPDATE resume
SET title = #{title}, updated_at = #{date}
WHERE id = #{id}

UPDATE resume
SET resume_col = #{col}, resume_row = #{row}
WHERE id = #{resumeId};
